const Auth = ({ navigation }) => {
  const { status } = requestLocationPermission();

  const [user, setUser] = useState<IUser>();
  const [isLoading, setIsLoading] = useState(false);
  const [skipLogin, setSkipLogin] = useState(false);

  const { promptAsync, request, getUserData, accessToken, userInfo } =
    useAuthentication();

  const getOrAddUser = async () => {
    try {
      setIsLoading(true);
      const currentUser = await addUser({
        username: userInfo?.name || "",
        email: userInfo?.email || "",
        picture: userInfo?.picture || "",
      });
      setUser(currentUser);
    } catch (e) {
      console.log(e);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    if (userInfo) getOrAddUser();
  }, [userInfo]);

  if (isLoading)
    return (
      <View style={styles.container}>
        <CircularLoader />
      </View>
    );
  return (
    <UserProvider>
      {user || skipLogin ? (
        status?.granted ? (
          <Location />
        ) : (
          <AllowLocation
            username={capitalize(userInfo?.name || "") || ""}
            navigation={navigation}
            currentLocationPermission={status}
          />
        )
      ) : (
        <SafeAreaView style={styles.container}>
          <View style={{ marginTop: "10%" }}>
            <WelcomeImage />
          </View>
          <View style={styles.labelWrapper}>
            <Typography fontSize="xxl" weight="900">
              DayTrip
            </Typography>
            <Typography
              fontSize="l"
              weight="500"
              style={{ marginVertical: 12 }}
            >
              Every day is a new adventure
            </Typography>
            <Typography
              fontSize="l"
              color={GRAY_LIGHT}
              style={{
                textAlign: "center",
              }}
            >
              Wherever your are, whenever you want to start your day - you’re
              just a few clicks away from the BEST vacation!
            </Typography>
          </View>
          <Button
            style={styles.loginButton}
            title="Login and start the DayTrip planning!"
            disabled={!request}
            onPress={() => promptAsync()}
          />
          <Pressable
            onPress={() => setSkipLogin(true)}
            style={{ marginBottom: 24 }}
          >
            <Typography style={{ textDecorationLine: "underline" }}>
              Continue as a guest
            </Typography>
          </Pressable>
        </SafeAreaView>
      )}
    </UserProvider>
  );
};

